// File: contracts/public-key-store/PublicKeyStoreV2.sol

// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

/**
 This rendition of a public key isn't revealing the public encryption key associated with the address.
 Instead, it presents an individual distinct public key.
 Alongside this public key, the confidential key is preserved in the contract and encrypted using the public encryption key attached to the address.
*/

contract PublicKeyStoreV2
{
    mapping(address => string) private publicKeyMap;
    mapping(address => string) private encSecretKeyMap;

    function setKeys(string memory _publicKey, string memory _encSecretKey) public
    {
        require(bytes(encSecretKeyMap[msg.sender]).length == 0, 'Keys already exist!');
        publicKeyMap[msg.sender] = _publicKey;
        encSecretKeyMap[msg.sender] = _encSecretKey;
    }

    function getEncSecretKey() public view returns (string memory)
    {
        return encSecretKeyMap[msg.sender];
    }

    function getPublicKey(address _address) public view returns (string memory)
    {
        return publicKeyMap[_address];
    }
}
